import React, { useState } from "react";


export const Post = ({ id, key, title, content, email, onEdit, onDelete }) => {
  const [isEdit, setIsEdit] = useState(false);

  const handleEdit = () => {
    setIsEdit(isEdit);
  };

  const handleDelete = () => {
    onDelete(id);
  };

  const handleOnEditSubmit = (evt) => {
    evt.preventDefault();
    onEdit(id, evt.target.name.value, evt.target.body.value, evt.target.email.value)
  }

  return (
    <div className="post-wrap">
      {isEdit ? (
        <div className="post-item">
          <div className="item-content">
            <div className="item-body">
              <h3>{title}</h3>
              <p>{content}</p>
              <p>{email}</p>
              <div className="item-footer">
                <button onClick={handleEdit}>Edit</button>
                <button onClick={handleDelete}>Delete</button>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="post-item">
          <div className="item-content">
            <div className="item-body">
              <form onSubmit={handleOnEditSubmit}>
                <input placeholder="Title" defaultValue={title}></input>
                <input placeholder="Content" defaultValue={content}></input>
                <input placeholder="Email" defaultValue={email}></input>
                <button onSubmit={handleOnEditSubmit}>Update</button>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
